$.ajaxSetup({
    headers: {
        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content'),
    }
});

var Toast = Swal.mixin({
    toast: true,
    position: 'top-end',
    showConfirmButton: false,
    timer: 5000,
});

/* Swal Notifications */
    function swal_success ( message, title = 'Success!' ) {
        Swal.fire( title, message, 'success' );
    }

    function swal_error ( message = 'Oops!, Something went wrong.', title = 'Error!' ) {
        Swal.fire( title, message, 'error' );
    }

    function swal_warning ( message, title = 'Warning' ) {
        Swal.fire( title, message, 'warning' );
    }

    function swal_confirmation ( callback, title = 'Are you sure?', text = "You won't be able to revert this!" ) {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, remove it!'
        }).then((result) => {
            if (result.value) {
                callback();
            }
        })
    }
/* Swal Notifications */


/* Toast Notifications */
    function toast_success ( message, title = 'Success!' ) {
        Toast.fire({
            title: title,
            text: message,
            icon: 'success',
        });
    }

    function toast_error ( message = 'Oops!, Something went wrong.', title = 'Error!' ) {
        Toast.fire({
            title: title,
            text: message,
            icon: 'error',
        });
    }

    function toast_warning ( message, title = 'Warning' ) {
        Toast.fire({
            title: title,
            text: message,
            icon: 'warning',
        });
    }

    function toast_info ( message, title = 'Info' ) {
        Toast.fire({
            title: title,
            text: message,
            icon: 'info',
        });
    }
/* Toast Notifications */
