"use strict";

$(document).on('click', '.update-company-info', function(event) {
	event.preventDefault();
	let $form_data = $('#form-company-info').serializeArray();

	updateSettingData( SETTING_UPDATE_COMPANY_INFO_URL, $form_data );
});

let updateSettingData = ( $url, $data ) => {
	$.ajax({
		url: $url,
		type: 'post',
		dataType: 'json',
		data: $data,
		beforeSend: () => {
			// show loader
		},
		complete: response => {
			console.log('response', response)
		},
		error: response => {

		},
	});
	
};